import { useState, useEffect } from 'react';

export interface User {
  id: string;
  name: string;
  email: string;
}

export function useAuth() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Check if user is authenticated on component mount
    const token = localStorage.getItem('auth-token');
    const userData = localStorage.getItem('user-data');

    if (token && userData) {
      setIsAuthenticated(true);
      setUser(JSON.parse(userData));
    }

    setIsLoading(false);
  }, []);

  const login = (userData: User) => {
    try {
      // Simulate login - in a real app, this would make an API call
      localStorage.setItem('auth-token', 'demo-token-' + Date.now());
      localStorage.setItem('user-data', JSON.stringify(userData));
      setIsAuthenticated(true);
      setUser(userData);
      return Promise.resolve();
    } catch (error) {
      return Promise.reject(error);
    }
  };

  const logout = () => {
    localStorage.removeItem('auth-token');
    localStorage.removeItem('user-data');
    setIsAuthenticated(false);
    setUser(null);
  };

  const signup = (userData: Omit<User, 'id'>) => {
    try {
      // Simulate signup - in a real app, this would make an API call
      const newUser: User = {
        ...userData,
        id: 'user_' + Date.now()
      };

      return login(newUser);
    } catch (error) {
      return Promise.reject(error);
    }
  };

  return {
    isAuthenticated,
    user,
    isLoading,
    login,
    logout,
    signup
  };
}

client/src/hooks/use-job-notifications.t.content:
import { useEffect, useRef } from "react";
import { useJobs } from "./use-jobs";
import { toast } from "./use-toast";
import type { Job } from "@shared/schema";

export function useJobNotifications() {
  const { data: jobsData } = useJobs(1, 100);
  const jobs = jobsData?.jobs || [];
  const prevJobsRef = useRef<Job[]>([]);

  useEffect(() => {
    const prevJobs = prevJobsRef.current;

    if (prevJobs.length === 0) {
      prevJobsRef.current = jobs;
      return;
    }

    // Check for newly completed jobs
    jobs.forEach(currentJob => {
      const prevJob = prevJobs.find(job => job.id === currentJob.id);

      if (prevJob && prevJob.status !== currentJob.status) {
        // Job status changed
        if (currentJob.status === "done") {
          toast({
            title: "Job Completed Successfully! ‚úÖ",
            description: `${currentJob.name} has finished running on ${currentJob.backend}`,
            duration: 5000,
          });
        } else if (currentJob.status === "failed") {
          toast({
            title: "Job Failed ‚ùå",
            description: `${currentJob.name} failed on ${currentJob.backend}`,
            duration: 7000,
            variant: "destructive",
          });
        } else if (currentJob.status === "running" && prevJob.status === "queued") {
          toast({
            title: "Job Started üöÄ",
            description: `${currentJob.name} is now running on ${currentJob.backend}`,
            duration: 3000,
          });
        }
      } else if (!prevJob && currentJob.status !== "queued") {
        // New job that's not in queue (likely just created)
        if (currentJob.status === "running") {
          toast({
            title: "New Job Started üöÄ",
            description: `${currentJob.name} is running on ${currentJob.backend}`,
            duration: 3000,
          });
        }
      }
    });

    prevJobsRef.current = jobs;
  }, [jobs]);

  // Return notification counts for UI
  const recentCompletedJobs = jobs.filter(job => 
    (job.status === "done" || job.status === "failed") && 
    job.endTime && 
    new Date(job.endTime).getTime() > Date.now() - 24 * 60 * 60 * 1000
  );

  const runningJobs = jobs.filter(job => job.status === "running");
  const queuedJobs = jobs.filter(job => job.status === "queued");

  return {
    recentCompletedJobs,
    runningJobs,
    queuedJobs,
    totalNotifications: recentCompletedJobs.length + runningJobs.length,
  };
}

client/src/hooks/use-jobs.ts.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import type { Job, InsertJob, JobStatus, Backend, Session } from "@shared/schema";

export function useJobs(page = 1, limit = 10) {
  return useQuery({
    queryKey: ["/api/jobs", page, limit],
    queryFn: async () => {
      const response = await fetch(`/api/jobs?page=${page}&limit=${limit}`);
      if (!response.ok) throw new Error("Failed to fetch jobs");
      return response.json() as Promise<{
        jobs: Job[];
        pagination: {
          currentPage: number;
          totalPages: number;
          totalJobs: number;
          limit: number;
        };
      }>;
    },
    refetchInterval: 8000, // Refetch every 8 seconds for more live updates
  });
}

export function useSearchJobs(query: string) {
  return useQuery({
    queryKey: ["/api/jobs/search", query],
    queryFn: async () => {
      if (!query) return [];
      const response = await fetch(`/api/jobs/search?q=${encodeURIComponent(query)}`);
      if (!response.ok) throw new Error("Failed to search jobs");
      return response.json() as Promise<Job[]>;
    },
    enabled: query.length > 0,
  });
}

export function useJobStats() {
  return useQuery({
    queryKey: ["/api/analytics/stats"],
    queryFn: async () => {
      const response = await fetch("/api/analytics/stats");
      if (!response.ok) throw new Error("Failed to fetch stats");
      return response.json() as Promise<{
        totalJobs: number;
        runningJobs: number;
        queuedJobs: number;
        completedJobs: number;
        failedJobs: number;
        successRate: number;
      }>;
    },
    refetchInterval: 3000, // Refetch every 3 seconds for live feel
  });
}

export function useJobTrends() {
  return useQuery({
    queryKey: ["/api/analytics/trends"],
    queryFn: async () => {
      const response = await fetch("/api/analytics/trends");
      if (!response.ok) throw new Error("Failed to fetch trends");
      return response.json() as Promise<Array<{
        date: string;
        count: number;
        label: string;
      }>>;
    },
    refetchInterval: 10000, // Refetch every 10 seconds for live trends
  });
}

export function useBackends() {
  return useQuery({
    queryKey: ["/api/backends"],
    queryFn: async () => {
      const response = await fetch("/api/backends");
      if (!response.ok) throw new Error("Failed to fetch backends");
      return response.json() as Promise<Backend[]>;
    },
    refetchInterval: 15000, // Refetch every 15 seconds
  });
}

export function useSessions() {
  return useQuery({
    queryKey: ["/api/sessions"],
    queryFn: async () => {
      const response = await fetch("/api/sessions");
      if (!response.ok) throw new Error("Failed to fetch sessions");
      return response.json() as Promise<Session[]>;
    },
  });
}

export function useCreateJob() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async (job: InsertJob) => {
      return await apiRequest("POST", "/api/jobs", job);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/jobs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/stats"] });
    },
  });
}

export function useUpdateJobStatus() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({ id, status, error }: { id: string; status: JobStatus; error?: string }) => {
      return await apiRequest("PATCH", `/api/jobs/${id}/status`, { status, error });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/jobs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/stats"] });
    },
  });
}

export function useDeleteJob() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/jobs/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/jobs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics/stats"] });
    },
  });
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

