                          <div className="flex justify-between">
                            <span className="text-sm font-medium">Last Calibrated:</span>
                            <span className="text-sm text-gray-600 dark:text-gray-400">31 minutes ago</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </TabsContent>
              </Tabs>
            </CardContent>
          </Card>
        </motion.div>
      </div>
    </AnimatePresence>
  );
}

